import React, { useState, useEffect } from 'react';
import axios from 'axios';
// import './Cart.css'; // File CSS cho giao diện giỏ hàng

function Cart() {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Lấy thông tin giỏ hàng từ server khi component được render
    axios.get('/getAllCart')
      .then((response) => {
        setCartItems(response.data);
        console.log(response)
      })
      .catch((error) => {
        console.error('Error fetching cart:', error);
      });
  }, []);

  // console.log('cartitem',cartItems)
  const removeFromCart = (courseId) => {
    // Xử lý logic xoá sản phẩm khỏi giỏ hàng
    axios.delete(`/api/cart/${courseId}`)
      .then((response) => {
        console.log(response.data.message);
        // Cập nhật lại danh sách giỏ hàng sau khi xoá
        const updatedCart = cartItems.filter(item => item.courseId !== courseId);
        setCartItems(updatedCart);
      })
      .catch((error) => {
        console.error('Error removing product from cart:', error);
      });
  };

  return (
    <div>
      <h1>Shopping Cart</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <ul>
          {cartItems.map((item,index) => (
            <li key={index}>
              <div>{item.name} - ${item.price}</div>
              <button onClick={()=>removeFromCart} >Remove</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}


export default Cart;
