import axios from "axios";
import React, { useState, useEffect } from "react";
import toast from "react-hot-toast";

export default function Payment() {
  const [totalPrice, setTotalPrice] = useState("");
  const [idOrder, setIdOrder] = useState("");

  useEffect(() => {
    axios
      .get("/getAllOrders")
      .then((response) => {
        setIdOrder(response.data[0]._id);
        setTotalPrice(response.data[0].totalAmount);
        console.log(response.data.totalAmount);
        console.log(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const handleCheckOut = async () => {
    try {
      
      const formData = {
        totalPrice,
        idOrder,
      };
      console.log("formData:", formData);

      const response = await axios.post("/checkout", formData, {
        mode: "no-cors", // Cấu hình yêu cầu không gửi preflight request
      });
      console.log("checkout success:", response.data);
      toast.success("Thanh toán thành công");
      // Điều hướng đến trang dashboard sau khi thanh toán thành công
      // navigate(`/dashboard`);
      const vnpayUrl = response.data.vnpUrl; // Thay thế bằng URL của VNPay cần chuyển hướng tới
      window.location.href = vnpayUrl;
    } catch (error) {
      console.error("Error during checkout:", error);
      toast.error("Lỗi thanh toán");
    }
  };

  return (
    <>
      <div>
        <h2>Đặt hàng thành công</h2>
        <h3>Đơn hàng: {idOrder}</h3>
        <p>Tổng tiền: {totalPrice}</p>
      </div>
      <button onClick={handleCheckOut}> Tiến hành thanh toán </button>
    </>
  );
}
