// import React from "react";
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
export default function DetailCourse() {


  // const [course, setCourse] = useState({});
  const [dataCourse, setDataCourse] = useState(
  
      {
        "_id": "65589fd7be6c0ae1b275be14",
        "name": "khóa học node js",
        "description": "Hiểu chi tiết về các khái niệm cơ bản trong JS\nXây dựng được website đầu tiên kết hợp với JS\nTự tin khi phỏng vấn với kiến thức vững chắc\nCó nền tảng để học các thư viện và framework JS\nNắm chắc các tính năng trong phiên bản ES6\nThành thạo DOM APIs để tương tác với trang web\nGhi nhớ các khái niệm nhờ bài tập trắc nghiệm\nNâng cao tư duy với các bài kiểm tra với testcases\nCác bài thực hành như Tabs, Music Player",
        "price": 20000,
        "image": {
            "_id": "65589fd7be6c0ae1b275be12",
            "filename": "image-1700306903427-712095344.png",
            "originalname": "download (1).png",
            "path": "public\\image\\image-1700306903427-712095344.png",
            "size": 3713,
            "mimetype": "image/png",
            "__v": 0
        },
        "postedBy": "65589fbabe6c0ae1b275be0c",
        "__v": 0
    
  }
  )
  const { idCourse } = useParams();


  useEffect(() => {
    const fetchcourse = async () => {
      try {
        const response = await axios.get(`/detail/${idCourse}`
        ); // Gọi API để lấy thông tin sản phẩm từ server
        // setCourse(response.data);
        console.log('course',response.data)
        setDataCourse(response.data);
        // console.log('data',data);
      } catch (error) {
        console.error("Error fetching course:", error);
      }
    };

    fetchcourse();
  },[]);

  if (!dataCourse[0]) {
    console.log('pass')
    return <div>Loading...</div>; // Hiển thị thông báo khi đang tải dữ liệu
    
  }
  
  return (
    
    <div className="container px-4 px-lg-5 my-5">
      <div className="row gx-4 gx-lg-5 align-items-center">
        {console.log(dataCourse[0])}
        <div className="col-md-6">
          <img
            className="card-img-top mb-5 mb-md-0"
            // src={`http://localhost:8000/image/${dataCourse.image.filename}`}
            src= {!dataCourse[0]?dataCourse[0].image.filename:"http://localhost:8000/image/image-1700306903427-712095344.png"}

            alt="..."
          />
        </div>


        <div className="col-md-6">
          <div className="small mb-1">SKU: BST-498</div>
          <h1 className="display-5 fw-bolder">{dataCourse.name}</h1>
          <div className="fs-5 mb-5">

            <span>Giá : {} VND</span>
          </div>
          <p className="lead">
            {/* {course.description} */}
          </p>
          <div className="d-flex">
            <input
              className="form-control text-center me-3"
              id="inputQuantity"
              type="num"
              defaultValue={1}
              style={{ maxWidth: "3rem" }}
            />
            <button
              className="btn btn-outline-dark flex-shrink-0"
              type="button"
            >
              <i className="bi-cart-fill me-1" />
              Add to cart
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
