// import React from "react";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
export default function DetailCourse() {
  // const [course, setCourse] = useState({});
  const [dataCourse, setDataCourse] = useState({});
  const { idCourse } = useParams();

  useEffect(() => {
    const fetchcourse = async () => {
      try {
        const response = await axios.get(`/detail/${idCourse}`); // Gọi API để lấy thông tin sản phẩm từ server
        // setCourse(response.data);
        console.log("course", response.data);
        setDataCourse(response.data);
        // console.log('data',data);
      } catch (error) {
        console.error("Error fetching course:", error);
      }
    };

    fetchcourse();
  }, [idCourse]);

  return 
  !dataCourse ? (
    <>
      console.log('pass') return <div>Loading...</div>; // Hiển thị thông báo
      khi đang tải dữ liệu
    </>
  ) : (
    <>
      <div className="container px-4 px-lg-5 my-5">
        <div className="row gx-4 gx-lg-5 align-items-center">
          {console.log(dataCourse)}
          <div className="col-md-6">
            <img
              className="card-img-top mb-5 mb-md-0"
              // src={`http://localhost:8000/image/${dataCourse.image.filename}`}
              src={
                !dataCourse
                  ? dataCourse.image.filename
                  : "http://localhost:8000/image/image-1700306903427-712095344.png"
              }
              alt="..."
            />
          </div>

          <div className="col-md-6">
            <div className="small mb-1">SKU: BST-498</div>
            <h1 className="display-5 fw-bolder">{dataCourse.name}</h1>
            <div className="fs-5 mb-5">
              <span>Giá : {} VND</span>
            </div>
            <p className="lead">{/* {course.description} */}</p>
            <div className="d-flex">
              <input
                className="form-control text-center me-3"
                id="inputQuantity"
                type="num"
                defaultValue={1}
                style={{ maxWidth: "3rem" }}
              />
              <button
                className="btn btn-outline-dark flex-shrink-0"
                type="button"
              >
                <i className="bi-cart-fill me-1" />
                Add to cart
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
