import { NavLink } from "react-router-dom";
import { useAuth } from "../../context/auth";
import { useNavigate } from "react-router-dom";
// import Header from '../../pages/Header'

export default function Main() {
  const [auth, setAuth] = useAuth();
  const navigate = useNavigate();
  const handleLogout = () => {
    setAuth({ user: null, token: "", refreshToken: "" });
    localStorage.removeItem("auth");
    navigate("/login");
  };
  const loggedIn =
    auth.user !== null && auth.token !== "" && auth.refreshToken !== "" && auth.role !=="Admin";

  const AdminLoggedIn =
    auth.user !== null && auth.token !== "" && auth.refreshToken !== "" && auth.role == "Admin";
    
  const handlePostAdClick = () => {
    if (loggedIn) {
      navigate("/ad/create/sell/House");
    } else {
      navigate("/login");
    }
  };
  const handleCartClick = () => {
    if (loggedIn) {
      navigate("/Cart");
    } else {
      navigate("/login");
    }
  };
  const loginHandleButton = () => {
    navigate("/login");
  }
  const registerHandleButton = () => {
    navigate("/register");
  }
  const imageUrl =
    "https://fullstack.edu.vn/static/media/f8-icon.18cd71cfcfa33566a22b.png";

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-light bg-light ml-4">
        <img src={imageUrl} width="38px" height="38px" />
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon" />
        </button>
        <div
          className="collapse navbar-collapse justify-content-between"
          id="navbarSupportedContent"
        >
          <ul className="navbar-nav ml-4">
            <li>
              <NavLink className="nav-link" to="/">
                Home
              </NavLink>
            </li>

            <li>
              <a onClick={handleCartClick} className="nav-link" to="/Cart">
                Cart
              </a>
            </li>
          </ul>
          <form className="form-inline mx-auto">
            <div className="input-group">
              <input
                className="form-control"
                type="search"
                placeholder="Search"
                aria-label="Search"
              />
              <div className="input-group-append">
                <button className="btn btn-outline-success" type="submit">
                  Search
                </button>
              </div>
            </div>
          </form>

          {!loggedIn ? (
        <>
         <div className="Login button">
          <button onClick={loginHandleButton} type="button" class="btn btn-outline-danger">
              Đăng Nhập
            </button>
            <button onClick={registerHandleButton} type="button" class="btn btn-outline-warning ">
              Đăng Kí
            </button>
          </div>
        </>
      ) : (
        ""
      )}
         
        </div>
      </nav>
      {AdminLoggedIn ? (
        <>
         <h1>this admin page</h1>
        </>
      ) : (
        ""
      )}
    </>
  );
}
