import React from "react";
import { NavLink } from "react-router-dom";
import { useAuth } from "../../context/auth";
import { useNavigate } from "react-router-dom";

const Main = () => {
  const [auth, setAuth] = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    setAuth({ user: null, token: "", refreshToken: "" });
    localStorage.removeItem("auth");
    navigate("/login");
  };

  const loggedIn =
    auth.user !== null &&
    auth.token !== "" &&
    auth.refreshToken !== "" &&
    auth.role !== "Admin";

  const AdminLoggedIn =
    auth.user !== null &&
    auth.token !== "" &&
    auth.refreshToken !== "" &&
    auth.role === "Admin";

  const handlePostAdClick = () => {
    if (loggedIn) {
      navigate("/ad/create/sell/House");
    } else {
      navigate("/login");
    }
  };

  const handleCartClick = () => {
    if (loggedIn) {
      navigate("/Cart");
    } else {
      navigate("/login");
    }
  };

  const loginHandleButton = () => {
    navigate("/login");
  };

  const registerHandleButton = () => {
    navigate("/register");
  };

  const imageUrl =
    "https://fullstack.edu.vn/static/media/f8-icon.18cd71cfcfa33566a22b.png";

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light ml-4">
      <img src={imageUrl} width="38px" height="38px" alt="icon" />
      <button
        className="navbar-toggler"
        type="button"
        data-toggle="collapse"
        data-target="#navbarSupportedContent"
        aria-controls="navbarSupportedContent"
        aria-expanded="false"
        aria-label="Toggle navigation"
      >
        <span className="navbar-toggler-icon" />
      </button>
      <div className="collapse navbar-collapse justify-content-between" id="navbarSupportedContent">
        <ul className="navbar-nav ml-4">
          <li className="nav-item">
            <NavLink className="nav-link" to="/">
              Home
            </NavLink>
          </li>
          <li className="nav-item">
            <a className="nav-link" onClick={handleCartClick}>
              Cart
            </a>
          </li>
        </ul>
        <form className="form-inline mx-auto">
          <div className="input-group">
            <input
              className="form-control"
              type="search"
              placeholder="Search"
              aria-label="Search"
            />
            <div className="input-group-append">
              <button className="btn btn-outline-success" type="submit">
                Search
              </button>
            </div>
          </div>
        </form>
        {!loggedIn ? (
          // Phần này hiển thị khi chưa đăng nhập
          <div className="Login button">
            <button onClick={loginHandleButton} className="btn btn-outline-danger">
              Đăng Nhập
            </button>
            <button onClick={registerHandleButton} className="btn btn-outline-warning">
              Đăng Kí
            </button>
          </div>
        ) : (
          // Phần này hiển thị khi đã đăng nhập
          <div className="dropdown">
            <button
              className="btn btn-secondary dropdown-toggle"
              type="button"
              id="dropdownMenuButton"
              data-toggle="dropdown"
              aria-haspopup="true"
              aria-expanded="false"
            >
              Welcome{" "}
              {auth?.user?.name ? auth.user.name : auth.user.username}
            </button>
            <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
              <Link className="dropdown-item" to={`/dashboard`}>
                Course Manager
              </Link>
              <Link
                onClick={handlePostAdClick}
                className="dropdown-item"
                to="/ad/create/sell/House"
              >
                Post Course
              </Link>
              <Link className="dropdown-item" to={`/Order`}>
                Check your Order
              </Link>
              <div
                className="dropdown-item"
                onClick={handleLogout}
                style={{ cursor: "pointer" }}
              >
                Logout
              </div>
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};
