import { NavLink } from "react-router-dom";
import { useAuth } from "../../context/auth";
import { useNavigate } from "react-router-dom";
import {Link} from 'react-router-dom'
export default function Main() {
  const [auth, setAuth] = useAuth();
  const navigate = useNavigate();
  const handleLogout = () => {
    setAuth({ user: null, token: "", refreshToken: "" });
    localStorage.removeItem("auth");
    navigate("/login");
  };
  const loggedIn =
    auth.user !== null && auth.token !== "" && auth.refreshToken !== "";

  const handlePostAdClick = () =>{
    if(loggedIn){
      navigate("/ad/create/sell/House");
    }
    else{
      navigate("/login");
    }

  }
  return (
    <ul className="nav d-flex justify-content-between p-2 lead">
      <li>
        <NavLink className="nav-link" to="/">
         <h1>Home</h1> 
        </NavLink>
        
      </li>
      <li>
        <a onClick={handlePostAdClick} className="nav-link" to="/ad/create/sell/House">
          <h1>Post Course</h1> 
        </a>
      </li>
      <li>
        <Link onClick={handlePostAdClick} className="nav-link" to="/">
         <h1>Cart</h1> 
        </Link>
        
      </li>
      {!loggedIn ? (
        <>
          <NavLink className="nav-link" to="/login">
           <h1>Login</h1> 
          </NavLink>
          <NavLink className="nav-link" to="/register">
            <h1>Register</h1>
          </NavLink>
        </>
      ) : (
        ""
      )}

      {loggedIn ? (
        <>
          <div className="dropdown">
            <li>
              <a
                className="nav-link pointer dropdown-toggle"
                data-bs-toggle="dropdown"
              >
                Welcome {auth?.user?.name ? auth.user.name : auth.user.username}
              </a>
              <ul className="dropdown-menu">
                <li>
                  <NavLink className="nav-link" to={`/dashboard`}>
                    Dashboard
                  </NavLink>
                </li>
                <li>
                  <a onClick={handleLogout} className="nav-link pointer">
                    Logout
                  </a>
                </li>
              </ul>
            </li>
          </div>
        </>
      ) : (
        ""
      )}
    </ul>
  );
}
