import axios from "axios";
import React, { useState, useEffect } from "react";
import { Button, Container, Row, Col } from "react-bootstrap";
import toast from "react-hot-toast";

export default function Payment() {
  const [totalPrice, setTotalPrice] = useState("");
  const [idOrder, setIdOrder] = useState("");

  useEffect(() => {
    axios
      .get("/getAllOrders")
      .then((response) => {
        setIdOrder(response.data[0]._id);
        setTotalPrice(response.data[0].totalAmount);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const handleCheckOut = async () => {
    try {
      const formData = {
        totalPrice,
        idOrder,
      };

      const response = await axios.post("/checkout", formData, {
        mode: "no-cors",
      });
      
      toast.success("Thanh toán thành công");
      
      const vnpayUrl = response.data.vnpUrl;
      window.location.href = vnpayUrl;
    } catch (error) {
      console.error("Error during checkout:", error);
      toast.error("Lỗi thanh toán");
    }
  };

  return (
    <div className="container mt-5" style={{height:'500px'}}>
      <div className="row justify-content-center">
        <div className="col-md-8">
          <div className="card">
            <div className="card-body text-center">
              <h1 className="card-title">Thanh toán thành công!</h1>
              <h1 className="card-title">Thanh toán thành công!</h1>
              <h1 className="card-title">Thanh toán thành công!</h1>
              <Button onClick={handleCheckOut} variant="primary">
              Tiến hành thanh toán
            </Button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
