import { useState } from "react";
import CurrencyInput from "react-currency-input-field";
import ImageUpload from "./ImageUpload";
export default function AdForm({ action, type }) {
  const [ad, setAd] = useState({
    photo: "",
    name:"",
    price: "",
    store: "",
    author: "",
    company: "",
    description: "",
    language: "",
    type: "",
    action: "",
    description: "",
    
  });

  const handleSubmit = async (e) => {
    try {
      const response = await axios.post('http://localhost:3001/create-product', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log('Product created:', response.data);
    } catch (error) {
      console.error('Error creating product:', error);
    }

  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label>Choose Image</label>
      <input
        type="file"
       
        className="form-control mb-3"
        onChange={(e) => setAd({ ...ad, photo: e.target.files[0] })}
        required
      />
      {/* <Image /> */}
        <CurrencyInput
          placeholder="Enter price"
          defaultValue={ad.price}
          className="form-control mb-3"
          onValueChange={(value) => setAd({ ...ad, price: value })}
        />
     

      <input
        type="number"
        min="0"
        className="form-control mb-3"
        placeholder="Enter your namme of book..."
        value={ad.name}
        onChange={(e) => setAd({ ...ad, name: e.target.value })}
        required
      />

      <input
        type="number"
        min="0"
        className="form-control mb-3"
        placeholder="Enter how many book in store..."
        value={ad.store}
        onChange={(e) => setAd({ ...ad, store: e.target.value })}
        required
      />


      <input
        type="text"
        className="form-control mb-3"
        placeholder="Language ..."
        value={ad.language}
        onChange={(e) => setAd({ ...ad, language: e.target.value })}
      />

      <input
        type="text"
        className="form-control mb-3"
        placeholder="Enter company"
        value={ad.company}
        onChange={(e) => setAd({ ...ad, company: e.target.value })}
        required
      />

      <textarea
        className="form-control mb-3"
        value={ad.description}
        placeholder="Write description"
        onChange={(e) => setAd({ ...ad, description: e.target.value })}
      />
      </form>
      <button className="btn btn-primary">Submit</button>
      <pre>{JSON.stringify(ad, null, 4)}</pre>
    </>
    
  );
}
