import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from "react-hot-toast";
// import './Cart.css'; // File CSS cho giao diện giỏ hàng
import { useNavigate } from "react-router-dom";
function Cart() {
  const [cartItems, setCartItems] = useState([]);
  const navigate = useNavigate();
  const [itemCount, setItemCount] = useState(0);
  useEffect(() => {
    // Lấy thông tin giỏ hàng từ server khi component được render
    axios.get('/getAllCart')
      .then((response) => {
        setCartItems(response.data);
        console.log(response)
        setItemCount(response.data.length); // Cập nhật số lượng sản phẩm
      })
      .catch((error) => {
        console.error('Error fetching cart:', error);
      });
  }, [itemCount]);

  // console.log('cartitem',cartItems)
  const removeFromCart = (courseId) => {
    // Xử lý logic xoá sản phẩm khỏi giỏ hàng
    axios.delete(`/cart/${courseId}`)
      .then((response) => {
        console.log(response.data.message);
        // Cập nhật lại danh sách giỏ hàng sau khi xoá
        // const updatedCart = cartItems.filter(item => item.courseId !== courseId);
        // setCartItems(updatedCart);
        // updateItemCount();
        navigate("/");
        toast.success("Deleted successful");
      })
      .catch((error) => {
        console.error('Error removing product from cart:', error);
      });
  };

  return (
    <div>
      <h1>Shopping Cart</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <ul>
          {cartItems.map((item,index) => (
            <li key={index}>
              <div>{item.name} - ${item.price}</div>
              <button onClick={()=>removeFromCart(item._id)} >Remove</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}


export default Cart;
