import React, { useEffect, useState } from "react";
import axios from "axios";
import { NavLink } from "react-router-dom";
import { useAuth } from "../../context/auth";
import { useNavigate } from "react-router-dom";

const Main = () => {
  const [auth, setAuth] = useAuth();
  const navigate = useNavigate();

  // Function để logout
  const handleLogout = () => {
    setAuth({ user: null, token: "", refreshToken: "" });
    localStorage.removeItem("auth");
    navigate("/login");
  };

  const loggedIn =
    auth.user !== null && auth.token !== "" && auth.refreshToken !== "" && auth.role !== "Admin";

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-light bg-light ml-4">
        {/* ... */}
        <div className="collapse navbar-collapse justify-content-between" id="navbarSupportedContent">
          <ul className="navbar-nav ml-4">
            <li>
              <NavLink className="nav-link" to="/">
                Home
              </NavLink>
            </li>
            <li>
              <a className="nav-link" href="/Cart">
                Cart
              </a>
            </li>
            {/* ... */}
          </ul>
          <form className="form-inline mx-auto">
            {/* Ô tìm kiếm */}
          </form>
          {/* Nút Đăng Nhập và Đăng Ký */}
          {!loggedIn ? (
            <div className="Login button">
              <button onClick={() => navigate("/login")} type="button" className="btn btn-outline-danger mr-2">
                Đăng Nhập
              </button>
              <button onClick={() => navigate("/register")} type="button" className="btn btn-outline-warning">
                Đăng Ký
              </button>
            </div>
          ) : (
            ""
          )}
          {/* Dropdown cho Người dùng đã Đăng Nhập */}
          {loggedIn ? (
            <div className="dropdown">
              <button
                className="btn btn-secondary dropdown-toggle"
                type="button"
                id="dropdownMenuButton"
                data-toggle="dropdown"
                aria-haspopup="true"
                aria-expanded="false"
              >
                Welcome {auth?.user?.name ? auth.user.name : auth.user.username}
              </button>
              <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                <NavLink className="dropdown-item" to={`/dashboard`}>
                  Course Manager
                </NavLink>
                <NavLink className="dropdown-item" to="/ad/create/sell/House">
                  Post Course
                </NavLink>
                <NavLink className="dropdown-item" to={`/Order`}>
                  Check your Order
                </NavLink>
                <button onClick={handleLogout} className="dropdown-item">
                  Logout
                </button>
              </div>
            </div>
          ) : (
            ""
          )}
        </div>
      </nav>
      {/* ... */}
    </>
  );
};

export default Main;
