import React, { useEffect, useState } from 'react';
import axios from 'axios';

const PaymentReturnPage = () => {
  const [paymentStatus, setPaymentStatus] = useState('');
  const [redirect, setRedirect] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Thay đổi cách gửi yêu cầu Axios để truyền dữ liệu nếu cần
        const response = await axios.get('/api/vnpay_return', {
          // Thêm các thông tin yêu cầu nếu cần (ví dụ: params, headers)
          // params: {
          //   // Thêm các tham số nếu cần
          // }
        });
  
        const { data } = response;
        console.log('data', data);
  
        if (data.msg) {
          setPaymentStatus(data.msg);
          setRedirect(data.redirect);
        }
      } catch (error) {
        console.error('Lỗi khi xử lý phản hồi thanh toán: ', error);
        // Xử lý lỗi tại đây nếu cần thiết
      }
    };
  
    fetchData();
  }, []);
  

  return (
    <div>
      {paymentStatus && (
        <div>
          <h2>Thông báo thanh toán</h2>
          <p>{paymentStatus}</p>
          {redirect && <a href={redirect}>Chuyển hướng đến trang cần thiết</a>}
        </div>
      )}
    </div>
  );
};

export default PaymentReturnPage;
