import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function EditCourse() {
  // const [course, setCourse] = useState({});
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [file, setFile] = useState(null);
  const [previewURL, setPreviewURL] = useState(null);
  const { idCourse } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`/detail/${idCourse}`);
        const { data } = response;

        setCourse(data); // Cập nhật state course với dữ liệu từ API
        setName(data.name); // Gán giá trị từ dữ liệu API vào state name
        setDescription(data.description); // Gán giá trị từ dữ liệu API vào state description
        setPrice(data.price); // Gán giá trị từ dữ liệu API vào state price
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, [idCourse]);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    const reader = new FileReader();
    reader.onloadend = () => {
      setPreviewURL(reader.result);
    };
    if (selectedFile) {
      reader.readAsDataURL(selectedFile);
    } else {
      setPreviewURL(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Xử lý submit form và gửi dữ liệu lên server
  };

  return (
    <div className="create-product-container offset-md-4 mt-4" style={{ height: "500px" }}>
      <h2>Edit Course</h2>
      <form onSubmit={handleSubmit} className="product-form">
        <div className="input-group">
          <label>Choose Image:</label>
          <input type="file" onChange={handleFileChange} />
        </div>
        <div className="input-group">
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>
        <div className="input-group">
          <label>Description:</label>
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <div className="input-group">
          <label>Price:</label>
          <input
            type="text"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
        </div>
        <button type="submit" className="submit-button">
          Save
        </button>
      </form>
    </div>
  );
}
