import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Table, Button, Container } from 'react-bootstrap';

const UserTable = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Fetch user data from API
    axios.get('/getAllUsers').then((response) => {
      setUsers(response.data);
    });
  }, []);

  const handleAdd = () => {
    // Logic to handle adding user
    console.log('Add user clicked');
  };

  const handleEdit = (userId) => {
    // Logic to handle editing user with userId
    console.log(`Edit user ${userId} clicked`);
  };

  const handleDelete = (userId) => {
    // Logic to handle deleting user with userId
    console.log(`Delete user ${userId} clicked`);
  };

  return (
    <Container className="mt-4">
      <h2>User Table</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Username</th>
            <th>Name</th>
            <th>Email</th>
            <th>Address</th>
            <th>Phone</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.username}</td>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.address}</td>
              <td>{user.phone}</td>
              <td>{user.role}</td>
              <td>
                <Button variant="primary" onClick={() => handleEdit(user._id)}>
                  Edit
                </Button>
                <Button variant="danger" className="ml-2" onClick={() => handleDelete(user._id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <Button variant="success" onClick={handleAdd}>
        Add User
      </Button>
    </Container>
  );
};

export default UserTable;
