// Home.js
import { useAuth } from "../context/auth";
import axios from "axios";
import React, { useEffect, useState } from "react";

export default function Home() {
  // context
  // const [auth, setAuth] = useAuth();

  const [courses, setCourses] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("/getAllCourse");
        setCourses(response.data);
        // console.log(courses);
        console.log(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const addToCart = (courseid) => {
    axios.post('http://localhost:8000/api/add-to-cart', { courseid , postBy: course.postedBy._id })
      .then((response) => {
        console.log(response.data.message);
        // fetchCart();
      })
      .catch((error) => {
        console.error('Error adding product to cart:', error);
      });
  };
  return (
    <>
      <div>
        <h1 className="display-1 bg-primary text-light p-5">Home</h1>
        {/* <pre>{JSON.stringify(auth, null, 4)}</pre> */}
      </div>
      <ul className="product-list">
        {courses.map((course, index) => (
          <li className="" key={index}>
            <div className="card mt-4 ml-4" style={{ width: "18rem" }}>
              {/* <img className="card-img-top" src="..." alt="Card image cap" /> */}
              {course.image && (
                <img
                  src={`http://localhost:8000/image/${course.image.filename}`}
                  alt="course"
                  className="card-img-top"
                />
              )}
              <div className="card-body">
                <h5 className="card-title">{course.name}</h5>
                <p className="card-text">{course.description}</p>
                <p className="card-text">Giá: {course.price} vnd</p>
                <p className="card-text">
                  <i>
                    Đăng bởi : {course.postedBy.email}
                    {/* Giá: {course.price} vnd */}
                  </i>
                </p>
                <div className="button-container">
                  <a href="#" className="btn btn-primary">
                    Mua ngay
                  </a>
                  <a onClick={()=>addToCart(course._id)}  className="btn btn-danger">
                    Thêm giỏ hàng
                  </a>
                </div>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </>
  );
}
