import React, { useState, useEffect } from 'react';
import axios from 'axios';
// import './Cart.css'; // File CSS cho giao diện giỏ hàng

function Cart() {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Lấy thông tin giỏ hàng từ server khi component được render
    axios.get('http://localhost:3000/api/cart')
      .then((response) => {
        setCartItems(response.data);
      })
      .catch((error) => {
        console.error('Error fetching cart:', error);
      });
  }, []);

  const addToCart = (productId) => {
    axios.post('http://localhost:3000/api/add-to-cart', { productId })
      .then((response) => {
        console.log(response.data.message);
        fetchCart();
      })
      .catch((error) => {
        console.error('Error adding product to cart:', error);
      });
  };
  const removeFromCart = (productId) => {
    // Xử lý logic xoá sản phẩm khỏi giỏ hàng
    axios.delete(`http://localhost:3000/api/cart/${productId}`)
      .then((response) => {
        console.log(response.data.message);
        // Cập nhật lại danh sách giỏ hàng sau khi xoá
        const updatedCart = cartItems.filter(item => item.productId !== productId);
        setCartItems(updatedCart);
      })
      .catch((error) => {
        console.error('Error removing product from cart:', error);
      });
  };

  return (
    <div className="cart-container">
      <h1>Shopping Cart</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <ul className="cart-list">
          {cartItems.map((item) => (
            <li className="cart-item" key={item.productId}>
              <div className="item-info">
                <div className="item-name">{item.name}</div>
                <div className="item-price">${item.price}</div>
              </div>
              <button className="remove-button" onClick={() => removeFromCart(item.productId)}>Remove</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Cart;
