const Order = () => {
  const [orders, setOrders] = useState([]);
  const [orderDetail, setOrderDetail] = useState(null);
  const [selectedOrderId, setSelectedOrderId] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    axios
      .get("/getOrder")
      .then((response) => {
        setOrders(response.data);
      })
      .catch((error) => {
        console.error("Error fetching orders:", error);
      });
  }, []);

  const showPopup = (orderId) => {
    setSelectedOrderId(orderId);
    const selectedOrder = orders.find((order) => order._id === orderId);
    setOrderDetail(selectedOrder);
    setShowModal(true);
  };

  const handleClose = () => {
    setShowModal(false);
    setSelectedOrderId(null);
    setOrderDetail(null);
  };

  return (
    <div className="container-fluid orderlist">
      {/* ... */}
      <tbody>
        {orders.map((item, index) => (
          <tr
            key={index}
            onClick={() => showPopup(item._id)}
          >
            {/* ... */}
          </tr>
        ))}
      </tbody>

      <Modal show={showModal} onHide={handleClose}>
        {/* ... */}
        <Modal.Body>
          <p>Order ID: {selectedOrderId}</p>
          {orderDetail && (
            <>
              <p>Tên khách hàng: <strong>{orderDetail?.userId?.name}</strong></p>
              {/* Các thông tin khác của đơn hàng */}
            </>
          )}
        </Modal.Body>
        {/* ... */}
      </Modal>
    </div>
  );
};
