import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Table, Button, Container } from 'react-bootstrap';

const CourseTable = () => {
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    // Fetch course data from API
    axios.get('/getAllCourse').then((response) => {
      setCourses(response.data);
    });
  }, []);

  const handleAdd = () => {
    // Logic to handle adding course
    console.log('Add course clicked');
  };

  const handleEdit = (courseId) => {
    // Logic to handle editing course with courseId
    console.log(`Edit course ${courseId} clicked`);
  };

  const handleDelete = (courseId) => {
    // Logic to handle deleting course with courseId
    console.log(`Delete course ${courseId} clicked`);
  };

  return (
    <Container className="mt-4">
      <h2>Course Table</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Price(VND)</th>
            <th>Image</th>
            <th>Posted By</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {courses.map((course) => (
            <tr key={course?._id}>
              <td>{course?.name}</td>
              <td>{course?.description}</td>
              <td>{course?.price}</td>
              <td>
              {course.image && (
                <div className="image-container">
                <img 
                  
                  src={`http://localhost:8000/image/${course.image.filename}`}
                  alt="course"
                  className="card-img-top rounded-image"
                />
                </div>
              )}
              </td>
              <td>{course?.postedBy.email}</td>
              <td>
                <Button variant="primary" onClick={() => handleEdit(course?._id)}>
                  Edit
                </Button>
                <Button variant="danger" className="ml-2" onClick={() => handleDelete(course?._id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <Button variant="success" onClick={handleAdd}>
        Add Course
      </Button>
    </Container>
  );
};

export default CourseTable;
