import React, { useState, useEffect } from "react";
import axios from "axios";
import { Container, Form, Button } from "react-bootstrap";
import { useNavigate, useParams } from "react-router-dom";
import toast from "react-hot-toast";

const AdminEditUser = () => {
  const [username, setUsername] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [phone, setPhone] = useState("");

  const navigate = useNavigate();
  const { idUser } = useParams();

  useEffect(() => {
    const fetchAdminEditUser = async () => {
      try {
        const response = await axios.get(`/profile/${idUser}`);
        setUsername(response.data.username);
        setName(response.data.name);
        setEmail(response.data.email);
        setAddress(response.data.address);
        setPhone(response.data.phone);
      } catch (error) {
        console.log(error);
      }
    };

    fetchAdminEditUser();
  }, [idUser]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = {
      username,
      name,
      address,
      phone,
    };

    try {
      const response = await axios.put(`/update-profile/${idUser}`, formData);
      console.log("Profile updated:", response.data);
      toast.success("Profile Updated Successfully");
      navigate(`/profile/${idUser}`);
    } catch (error) {
      if (error.response) {
        console.error("Error updating profile:", error.response.data);
        toast.error(error.response.data.message || "Error updating profile");
      } else if (error.request) {
        console.error("Error sending request:", error.request);
        toast.error("Error sending request, please try again.");
      } else {
        console.error("Error:", error.message);
        toast.error("An error occurred, please try again later.");
      }
    }
  };

  return (
    <Container className="mt-4 mb-5">
      <h2>Admin Update User</h2>
      <Form onSubmit={handleSubmit}>
        {/* ...Các input fields ở đây */}
        <Button className="mt-4" variant="primary" type="submit">
          Update Profile
        </Button>
      </Form>
    </Container>
  );
};

export default AdminEditUser;
