import React from "react";

export default function AdCard({ ad }) {
    const addToCart = (courseId) => {
        axios
          .post("/add-to-cart", { courseId })
          .then((response) => {
            console.log(response.data);
            toast.success("Add to card successful");
          })
          .catch((error) => {
            console.error("Error adding product to cart:", error);
          });
      };
  return <>
   
          <li className="">
            <div className="card mt-4 ml-5 mb-4" style={{  width: "28rem" }}>
              {ad.image && (
                <div className="image-container">
                <img 
                  
                  src={`http://localhost:8000/image/${ad.image.filename}`}
                  alt="ad"
                  className="card-img-top rounded-image"
                />
                </div>
              )}
              <div className="card-container">
              <div className="card-body">
                <a href={`/adDetails/${ad._id}`}>
                <h3 className="card-title">{ad.name}</h3>
                </a>
                
                {/* <p className="text-container">{ad.description}</p> */}
                <p className="card-text">Giá: <strong>{ad.price}</strong>  vnd</p>
                <p className="card-text">
                  <i>
                    Đăng bởi : {ad.postedBy.email}
                    {/* Giá: {ad.price} vnd */}
                  </i>
                </p>
                <div className="button-container">
                  <a href="#" className="btn btn-primary">
                    Mua ngay
                  </a>
                  <a onClick={()=>addToCart(ad._id)}  className="btn btn-danger">
                    Thêm giỏ hàng
                  </a>
                </div>
              </div>
              </div>
            </div>
          </li>
  </>;
}
