import React, { useEffect, useState } from "react";
import axios from "axios";

const Order = () => {
  const [order, setOrder] = useState([]);
  const [selectedOrderId, setSelectedOrderId] = useState(null);

  useEffect(() => {
    // Lấy thông tin giỏ hàng từ server khi component được render
    axios
      .get("/getOrder")
      .then((response) => {
        setOrder(response.data);
        console.log(response.data);
      })
      .catch((error) => {
        console.error("Error fetching orders:", error);
      });
  }, []);

  const showPopup = (orderId) => {
    setSelectedOrderId(orderId);
    const modal = new window.bootstrap.Modal(document.getElementById("orderPopup"));
    modal.show();
  };

  const closePopup = () => {
    setSelectedOrderId(null);
    const modal = new window.bootstrap.Modal(document.getElementById("orderPopup"));
    modal.hide();
  };

  return (
    <div className="container-fluid orderlist">
      <h1 className="m-4">Check Your Order</h1>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Mã đơn hàng</th>
            <th scope="col">Thời gian đặt</th>
            <th scope="col">Trạng thái</th>
          </tr>
        </thead>
        <tbody>
          {order.map((item, index) => (
            <tr key={item._id} onClick={() => showPopup(item._id)}>
              <th scope="row">{index + 1}</th>
              <td>{item._id}</td>
              <td>{item.createdAt}</td>
              <td>Đang xử lý</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Bootstrap Modal Popup */}
      <div className="modal" id="orderPopup" tabIndex="-1" role="dialog">
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Order Details</h5>
              <button type="button" className="close" onClick={closePopup}>
                <span>&times;</span>
              </button>
            </div>
            <div className="modal-body">
              <p>Order ID: {selectedOrderId}</p>
              {/* Thêm các thông tin khác của đơn hàng ở đây */}
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" onClick={closePopup}>Close</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Order;
