import React from "react";
import Carousel from "react-bootstrap/Carousel";
export default function Slider() {
  var heroData = [
    {
      id: 1,
      image: 'https://fullstack.edu.vn/static/media/f8-icon.18cd71cfcfa33566a22b.png',
      title: "The perfect design for your website",
      description:
        "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ab suscipit dicta nulla. Consequuntur obcaecati officiis, labore doloribus non tempore impedit consequatur ab dolor. Explicabo quam repellendus vero omnis, nisi odio!",
      link: "https://www.google.com",
    },
    {
      id: 2,
      image: 'https://fullstack.edu.vn/static/media/f8-icon.18cd71cfcfa33566a22b.png',
      title: "Start Your Future Financial Plan",
      description:
        "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ab suscipit dicta nulla. Consequuntur obcaecati officiis, labore doloribus non tempore impedit consequatur ab dolor. Explicabo quam repellendus vero omnis, nisi odio!",
      link: "https://www.facebook.com",
    },
    {
      id: 3,
      image: 'https://fullstack.edu.vn/static/media/f8-icon.18cd71cfcfa33566a22b.png',
      title: "Enjoy the Difference",
      description:
        "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ab suscipit dicta nulla. Consequuntur obcaecati officiis, labore doloribus non tempore impedit consequatur ab dolor. Explicabo quam repellendus vero omnis, nisi odio!",
      link: "https://www.twitter.com",
    },
  ];

  const imageUrl =
    "https://fullstack.edu.vn/static/media/f8-icon.18cd71cfcfa33566a22b.png";
  return (
    <section id="home" className="hero-block">
       <Carousel>
          {
            heroData.map(hero => {
              return (
                <Carousel.Item key={hero.id}>
                  <img
                    className="d-block w-100"
                    src={hero.image}
                    alt={"slide " + hero.id}
                  />
                  <Carousel.Caption>
                    <h2>{hero.title}</h2>
                    <p>{hero.description}</p>
                    <a className="btn btn-primary" href={hero.link}>Learn More <i className="fas fa-chevron-right"></i></a>
                  </Carousel.Caption>             
                </Carousel.Item>
              );
            })
          }
      </Carousel>
    </section>
  );
}
